// JSONata node performance is better than Function nodes
// Don't use this function node.
// Leave here for illustration on parsing the telemetry data

// The telemetry data arrives from the drone as a single string:
// pitch:0;roll:-1;yaw:0;vgx:0;vgy:0;vgz:0;templ:68;temph:69;tof:10;h:0;bat:90;baro:-39.22;time:0;agx:-3.00;agy:20.00;agz:-995.00;

// Split the telemetry data into a json object that looks like this:
// { pitch:0, roll:-1, yaw:0, vgx:0, vgy:0, vgz:0, templ:68, temph:69, tof:10, 
//   h:0, bat:90, baro:-39.22, time:0, agx:-3.00, agy:20.00, agz:-995.00 }

// Use String functions to
// convert the  pitch:0;roll:-1; to "pitch":0,"roll":-1,"
// replace all occurrences of ; with ,"
// replace all occurrences of : with ",
var state = msg.payload.toString();

var jsonstr = "{\"" + state.replace(/;/g, ',"').replace(/:/g, '":').slice(0,-4) + "}";
// Now it looks like this:
// "{"pitch":0,"roll":-1,"yaw":0,"vgx":0,"vgy":0,"vgz":0,"templ":68,"temph":69,"tof":10,"h":0,"bat":90,"baro":-39.22,"time":0,"agx":-3.00,"agy":20.00,"agz":-995.00}"

msg.payload = JSON.parse(jsonstr);
return msg;

// Everythiing below this line, works too but is much more complicated.

// Split the string into an array of keypairs
var tellostate_array = msg.payload.split(';');
// 17 string elements in the array [ "pitch:0","roll:-1", ... ]

// Maybe there are better techniques...
// Build a stringified json object
var buildjson;
for( i=0; i < tellostate_array.length-1; i++) {
    // split the pitch:0 into a key:keyvalue
    var keypair = tellostate_array[i].split(":");
    if( i === 0 ) {
        buildjson = "{\""+keypair[0]+"\":"+keypair[1]+",";
    } else {
        buildjson = buildjson+"\""+keypair[0]+"\":"+keypair[1]+",";
    }
}
// JSON.parse() does not allow trailing commas so strip the last one off
buildjson = buildjson.slice(0,-1) + "}";
// Now it looks like this:
// "{"pitch":0,"roll":-1,"yaw":0,"vgx":0,"vgy":0,"vgz":0,"templ":68,"temph":69,"tof":10,"h":0,"bat":90,"baro":-39.22,"time":0,"agx":-3.00,"agy":20.00,"agz":-995.00}"
msg.payload = JSON.parse(buildjson);

return msg;

var telloaction ;

if((msg.payload.tellovalue) && (msg.payload.tellovalue != "0")) {
  telloaction = new Buffer( msg.payload.tellocmd + ' '+ msg.payload.tellovalue );
} else {
  telloaction = new Buffer( msg.payload.tellocmd );
}

msg.payload = telloaction;	
return msg;

var TELLO_CMD_ALT_LIMIT = 4182;
var TELLO_CMD_LIGHT_STRENGTH = 53;
var TELLO_CMD_LOW_BATT_THRESHOLD = 4183;
var TELLO_CMD_STATUS = 86;
var TELLO_CMD_LOG_HEADER_WRITE = 4176;
var TELLO_CMD_LOG_DATA_WRITE = 4177;
var TELLO_CMD_LOG_CONFIGURATION = 4178;
var seqID ;
var pacType;

function parsePacket (bb) {
    //	var data = Buffer.from(msg);
	var size = bb.length;
	var payload = bb.slice(9,size);
	var dataSize = 0;
	var cmdID    = 0;

	if (bb.length >= 11) {
			var mark = bb.readUInt8(0);
			if (mark == 0xCC) {	// start of packet
                size      = bb.readUInt16LE(1) >> 3;
                var crc8  = bb.readUInt8(3);
                pacType   = bb.readUInt8(4);
                cmdID     = bb.readUInt16LE(5);
                seqID     = bb.readUInt16LE(7);
                dataSize  = size - 11;
                var data  = false;
                var crc16 = bb.readUInt16LE(size - 2);
			} 

			if (cmdID == TELLO_CMD_STATUS) {
                //	this.parseStatus(bb);
            } else if (cmdID == TELLO_CMD_LOG_HEADER_WRITE) {
                node.warn("TELLO_CMD_LOG_HEADER_WRITE " + TELLO_CMD_LOG_HEADER_WRITE);
			} else if (cmdID == TELLO_CMD_LOG_DATA_WRITE) {
                // 
			} else if (cmdID == TELLO_CMD_ALT_LIMIT) {    
                node.warn("TELLO_CMD_ALT_LIMIT "+TELLO_CMD_ALT_LIMIT);
                if (payload.length > 0) 
                    node.warn(payload.readUInt16LE(1));
			} else if (cmdID == TELLO_CMD_LIGHT_STRENGTH) {    
                node.warn("TELLO_CMD_LIGHT_STRENGTH "+TELLO_CMD_LIGHT_STRENGTH);
                // if (payload.length > 0) 
                node.warn(payload.readUInt16LE(1));
                // node.warn(payload)
			} else if (cmdID == TELLO_CMD_LOW_BATT_THRESHOLD) {    
                node.warn("TELLO_CMD_LOW_BATT_THRESHOLD "+TELLO_CMD_LOW_BATT_THRESHOLD);
                if (payload.length > 0)
                    node.warn(payload.readUInt16LE(1));
			} else if (cmdID == 69 ) {    //VERSION
                node.warn("TELLO_CMD_VERSION "+cmdID);
                node.warn(payload);
                node.warn(payload.slice(1,20).toString());
			} else if (cmdID == TELLO_CMD_LOG_CONFIGURATION) {    
                node.warn("TELLO_CMD_LOG_CONFIGURATION command detected");
			} else {
                node.warn("DBG unknown CMD "+ cmdID);
			}
	} else {
        node.warn('wrong packet', bb);
	}
	return cmdID;
}

if( msg.payload.toString() == "ok" ) {
	msg.payload = "ok";
	return msg;
}

var batterystatus = global.get("tellorequestbattery") || false;
if ( batterystatus === true ) {
    msg.payload = { "battery":msg.payload.toString().trim() };
    global.set("tellorequestbattery", false);    
    return msg;
}

msg.cmdID  = parsePacket(msg.payload);
msg.seqID  = seqID;
msg.pacType= pacType;
global.set("tellomsg",msg);
return msg;
